        body {
            margin: 0px;
            font-family:  Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            font-size: 14px;
            line-height: 1.6em;
            color:#444444;
        }
        a:link,
        a:visited{
            color: #d0b357;
            font-weight: normal;
            text-decoration:none;
        }
        a:hover {
            color: #999999;
        }
        a img {
            border-width:0;
        }
        .post img,
        .sidebar img {
        }
        /* Headings
        ----------------------------------------------- */
        h1,
        h2,
        h3,
        h4,
        h5,
        h6 {
            font-family: Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif
            margin: 10px 0 10px;
            font-size: 18px;
            font-weight: normal;
        }
        h1 {
            font-size: 25px;
        }
        h2 {
            font-size: 20px;
        }
        h3 {
            font-size: 18px;
        }
        h4 {
            font-size: 15px;
        }
        h5 {
            font-size: 13px;
        }
        h6 {
            font-size: 12px;
        }
        /* Header
        -----------------------------------------------
        */
        #header-wrapper {
            width:100%;
            padding: 0px 0px 40px 0px;
            margin: 0px 0px 40px 0px;
            background: top center;
            overflow: hidden;
            background: url(http://3.bp.blogspot.com/-dYXKiOi14n4/VTXYMyePbUI/AAAAAAAAIyU/9th4N4kv4yI/s1600/header.png) repeat-x;
        }
        #header-inner {
            margin-left: auto;
            margin-right: auto;
            width: 100%;
        }
        #header-inner img {
            width: 100%;
            height: auto;
        }
        #header {
            text-align: center;
            font-weight: normal;
        }
        #header h1 {
            text-align: center;
            padding: 180px 0px 0px 0px;
            margin: 0px  0px 0px 0px;
            letter-spacing: 1px;
            text-transform: lowercase;
            font: 80px 'manhattan_darlingregular', Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            line-height: 50px;
            color: #d0b357;
        }
        #header a,
        #header a:visited{
            text-decoration:none;
            color: #d0b357;
        }
        #header a:hover {
        }
        #header .description {
            text-transform: uppercase;
            margin: 20px;
            padding: 0px 0px 0px 0px;
            text-align: center;
            letter-spacing: 2px;
            text-transform: uppercase;
            font: 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #d0b357;
        }
        /* Outer-Wrapper
        ----------------------------------------------- */
        #outer-wrapper {
            width: 1000px;
            margin:0px auto;
            padding:20px 0px 0px 0px;
            text-align:left;
            overflow: visible;
            font: Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #333333;
        }
        #main-wrapper {
            width: 650px;
            margin:25px 0px 0px 0px;
            float: left;
            padding: 0px 10px 0px 0px;
            word-wrap: break-word; /* fix for long text breaking sidebar float in IE */
            overflow: hidden;         /* fix for long non-text content breaking IE sidebar float */
            font: Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #333333;
        }
        #sidebar-wrapper {
            width: 300px;
            margin:0 0 0em;
            float: right;
            padding: 0px 0px 0px 0px;
            word-wrap: break-word; /* fix for long text breaking sidebar float in IE */
            overflow: visible !important;         /* fix for long non-text content breaking IE sidebar float */
            text-align: left;
        }
        /* Menu
        ----------------------------------------------- */
        .PageList {
            background-color: none;
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            width: 1000px;
            text-align:center !important;
            margin: 0px 0px 0px;
            padding: 0px 0px;
            text-transform: uppercase;
            letter-spacing:2px;
            padding-bottom:0px;
            padding-top:0px;
            margin: auto;
        }
        .PageList li {
            float: none !important;
            display: inline-block !important;
        }
        .PageList a,
        .PageList a:visited {
            color: #2c546d;
        }
        .PageList a:hover {
            color: #999999;
        }
        .PageList LI.selected A {
            font-weight: normal;
        }
        .crosscol .PageList LI, .footer .PageList LI {
            background: none repeat scroll 0 0 rgba(0, 0, 0, 0);
            float: left;
            list-style: none outside none;
            margin: 10px 15px;
            padding-right: 0px;
        }
        .PageList li:after{
            content: "//";
            margin-left: 30px;
            color: #d0b357;
        }
        .PageList li:last-child:after{
            display: none;
        }
        .PageList li a:hover {
            color: #999999;
        }
        #outer-PageList {
            background: #d0b357;
        }
        /* Blockquote
        ----------------------------------------------- */
        .post blockquote {
            margin: 1em 20px;
        }
        .post-body blockquote {
            line-height:21px;
            background: #fff;
            border: 1px solid #d0b357;
            padding:10px;
        }
        /* Posts
        -----------------------------------------------
        */
        h2.date-header {
            letter-spacing: 2px;
            margin: 0px 0px 0px 0px;
            text-transform: lowercase;
            font: italic 11px Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #2c546d;
            text-align:center;
            border:none;
        }
        .post {
            margin:0;
            padding-bottom:1em;
        }
        .post-title,
        .post-title a,
        .post-title a:visited,
        .entry-title,
        .entry-title a,
        .entry-title a:visited{
            text-transform: uppercase;
            text-align: center;
            margin: 5px 0px 10px 0px;
            padding: 0px 0px 0px 0px;
            color: #2c546d;
            font: normal normal 12px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            letter-spacing: 2px;
        }
        .post-title a:hover{
            color: #999999;
        }
        .post-body {
        }
        .post-footer {
            margin: 20px 0px 20px 0px;
            padding: 10px 0px 10px 0px;
            border-top: 1px solid #eee;
            text-align: center;
            text-transform: uppercase;
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            font-size: 10px;
            color: #2c546d;
            letter-spacing: 2px;
        }
        .comment-link {
            margin-left:.6em;
        }
        .post img {
            max-width: 650px;
            height: auto;
            padding-right:2px;
            padding-left:2px;
            border:0px solid #d0b357;
        }
        }
        /* Read More
        ----------------------------------------------- */
        .jump-link {
            color:#d0b357;
            display:block;
            margin-top:0px;
            margin-bottom:30px;
            margin-left:0px;
            text-align:center;
            float:center;
        }
        .jump-link a{
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color:#d0b357;
            display:block;
            text-transform: uppercase;
            text-align: center;
            letter-spacing: 2px;
            border: 1px solid #eee;
            width:150px;
            padding-top:9px;
            padding-bottom:9px;
            padding-left:15px;
            padding-right:15px;
            float:center;
        }
        .jump-link a:visited{
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color:#d0b357;
            display:block;
            text-transform: uppercase;
            text-align: center;
            letter-spacing: 2px;
            border: 1px solid #eee;
            width:150px;
            padding-top:9px;
            padding-bottom:9px;
            padding-left:15px;
            padding-right:15px;
            float:center;
        }
        .jump-link a:link{
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color:#d0b357;
            display:block;
            text-transform: uppercase;
            text-align: center;
            letter-spacing: 2px;
            border: 1px solid #eee;
            width:150px;
            padding-top:9px;
            padding-bottom:9px;
            padding-left:15px;
            padding-right:15px;
            float:center;
        }
        .jump-link a:active{
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color:#d0b357;
            display:block;
            text-transform: uppercase;
            text-align: center;
            letter-spacing: 2px;
            border: 1px solid #eee;
            width:150px;
            padding-top:9px;
            padding-bottom:9px;
            padding-left:15px;
            padding-right:15px;
            float:center;
        }
        .jump-link a:hover{
            color: #999999;
        }
        /* Comments
        ----------------------------------------------- */
        .comments {
            clear: both;
            margin-bottom: 0;
            margin-top: 0px;
        }
        .comments .comments-content .comment-replies {
            margin-top: 0;
        }
        #comments h4 {
            margin:1em 0;
            font-weight: normal;
            line-height: 1.4em;
            text-transform:auto;
            letter-spacing:0em;
            color: #333333;
        }
        #comments-block {
            margin:1em 0 1.5em;
            line-height:1.6em;
        }
        #comments-block .comment-author {
            margin:.5em 0;
        }
        #comments-block .comment-body {
            margin:.25em 0 0;
        }
        #comments-block .comment-footer {
            margin:-.25em 0 2em;
            line-height: 1.4em;
            text-transform:uppercase;
            letter-spacing:.1em;
        }
        #comments-block .comment-body p {
            margin:0 0 .75em;
        }
        .deleted-comment {
            font-style:italic;
            color:#333333;
        }
        #blog-pager-newer-link {
            float: right;
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            letter-spacing: 2px;
            padding-bottom: 20px;
            text-align: center;
            text-transform: uppercase;
        }
        #blog-pager-older-link {
            float: left;
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            letter-spacing: 2px;
            padding-bottom: 20px;
            text-align: center;
            text-transform: uppercase;
        }
        #blog-pager {
            text-align: center;
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            letter-spacing: 2px;
            padding-bottom: 20px;
            text-align: center;
            text-transform: uppercase;
        }
        .feed-links {
            clear: both;
            line-height: 2.5em;
        }
        /* Sidebar Content
        ----------------------------------------------- */
        .sidebar {
            font: Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #333333;
            line-height: 1.5em;
        }
        .sidebar img {
            max-width: 300px;
        }
        .sidebar ul {
            list-style:none;
            margin:0 0 0;
            padding:0 0 0;
        }
        .sidebar li {
            margin:0;
            padding-top:0;
            padding-right:0;
            padding-bottom:0em;
            padding-left:15px;
        }
        .sidebar h2 {
            text-align: center;
            letter-spacing: 2px;
            padding: 0px 0px;
            margin: 30px 0px 10px;
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #2c546d;
            text-transform: uppercase;
            padding: 10px 0px 10px 0px;
            background-color: none;
            border: 1px solid #d0b357;
        }
        .sidebar .widget {
            margin-bottom: 25px;
        }
        .BlogArchive #ArchiveList ul li {
            background: none repeat scroll 0 0 rgba(0, 0, 0, 0);
            border-width: 0;
            list-style: none outside none;
            margin: 0.25em 0;
            padding-left: 0px;
            text-indent: -15px;
            font: Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            text-align:center;
            line-height: 18px;
            text-transform: none;
        }
        /* Profile
        ----------------------------------------------- */
        .Profile {
            text-align: center;
        }
        .profile-img {
            float: none;
            width: 60%;
            height: auto;
            margin-top: 15px;
            -moz-border-radius: 150px;
            -webkit-border-radius: 150px;
            -khtml-border-radius: 150px;
            border-radius: 150px;
        }
        .profile-data {
            font: Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #333333;
            margin:0;
            padding-bottom: 0px;
            text-transform: lowercase;
            line-height: 1.5em;
        }
        .profile-datablock {
        }
        .profile-textblock {
        }
        .profile-link {
            display: none;
            text-transform: lowercase;
            letter-spacing: 2px;
            font: italic 11px Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #d0b357;
            text-align: center;
        }
        .profile-name-link {
            display:none;
            background-image:none !important;
            padding-left: 0px;
        }
        /* Footer
        ----------------------------------------------- */
        #footer {
            width:100%;
            padding-top:15px;
            text-align: center;
        }
        /* Follow by Email and Search
        ----------------------------------------------- */
        .FollowByEmail input[type="text"]{
        }
        .widget.CustomSearch .widget-content, .widget.FollowByEmail .widget-content{
            width:100%;
        }
        .widget .gsc-search-box table{
        }
        .widget form table{
            height: 38px;
            border-top: 1px solid #eee;
            border-bottom: 1px solid #eee;
            margin: 0 auto;
            height:38px;
        }
        .widget .gsc-search-box input[type="submit"]{
            width:35px;height:35px;
            top:-3px;
            background: url(http://4.bp.blogspot.com/-o_zw1k56ufg/VTIFUAbDgoI/AAAAAAAAIv0/HJLMb6-EjXo/s1600/search.png) center no-repeat !important;
            padding-left: 0px;
        }
        table.gsc-search-box td.gsc-input {
            padding-right: 0px !important;
        }
        .widget input[type="text"]:focus{
            outline: none;
        }
        .widget input[type="submit"], .FollowByEmail .follow-by-email-inner .follow-by-email-submit input[type="submit"] {
            background: url(http://1.bp.blogspot.com/--I9_tHhoIYY/VTIFTi0CeVI/AAAAAAAAIvo/3MzC2BfB_Kg/s1600/follow.png) center no-repeat !important;
            border:none !important;
            border-radius: 0 !important;
            -webkit-appearance: none;
            width: 55px !important;
            height: 36px !important;
            color:white !important;
            font-size:27px !important;
            cursor:pointer ;
            font-weight:normal;
            margin-left:0px !important;
            text-align: left;
            padding-left: 0px;
            overflow:hidden;
            text-indent: 200px;
            white-space: nowrap;
        }
        .widget input[type="text"]{
            border: none !important;
            padding:3px 0px;
            width:100%;
            height: 19px !important;
            font-size:13px !important;
            -webkit-appearance: none;
            border-radius:0;
            -webkit-border-radius:0px;
            text-align: left;
            margin-top:0px;
            margin-left:24px;
            background: none;
            font: Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            color: #333333;
            letter-spacing: 0px;
        }
        .widget form table.gsc-branding{
            display:none;
        }
        tr, table, form{
            position: relative;
        }
        .gsc-search-box tr {
            display:block;
        }
        .mobile .gsc-clear-button{
            background:none;
        }
        /* Popular posts
        ----------------------------------------------- */
        .PopularPosts {
            color:#333333;
            font:Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            line-height:1.5em;
        }
        .popular-posts .item-thumbnail img {
            display: block;
            float: center;
            margin-left: 0px;
            margin-right: 0px;
            padding:5px;
            height:72px;
            width:72px;
            -webkit-border-radius: 50em;
            -moz-border-radius: 50em;
            border-radius: 50em;
            margin-top:35px;
        }
        .item-title{
            background:none;
            color:#d0b357;
            margin-bottom:10px;
            text-align:left;
            font: normal normal 10px Montserrat, Georgia, Century Gothic, Trebuchet, Arial, Verdana, Sans-serif;
            font-weight:normal;
            text-transform:uppercase;
            letter-spacing:.2em;
            padding:5px;
        }
        .item-title a{
            background:none;
            color:#d0b357;
        }
        .item-title a:hover{
            background:none;
            color:#999999;
        }
        /* Post Share Icons
        ----------------------------------------------- */
        #share-wrap {
